import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import DynamicChatroom from "../../components/DynamicChatroom/DynamicChatroom.tsx";

function AdminPanel({ user }) {
    const [chatrooms, setChatrooms] = useState([]);
    const [loading, setLoading] = useState(true);;
    const [activeChatroomId, setActiveChatroomId] = useState(null);
    const [inviteCodeInput, setInviteCodeInput] = useState('');

    const fetchChatrooms = async () => {
        try {
            const response = await fetch(`http://localhost:443/user/roomids?customerId=${user.id}&customerEmail=${user.email}`);
            if (response.ok) {
                const data = await response.json();
                setChatrooms(data);
            } else {
                console.error('Failed to fetch chatrooms:', response.status);
            }
        } catch (error) {
            console.error('Error fetching chatrooms:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchChatrooms();
    }, [user.id]);

    const handleToggleChatroom = (roomId) => {
        if (chatrooms.some(chatroom => chatroom.roomId === roomId)) {
            setActiveChatroomId(roomId === activeChatroomId ? null : roomId);
        } else {
            setActiveChatroomId(null);
        }
    };

    const handleAddUserToChatroom = () => {
        const foundChatroom = chatrooms.find(chatroom => chatroom.inviteCode === inviteCodeInput);
        if (foundChatroom) {
            console.log(`User added to chatroom with ID: ${foundChatroom.roomId}`);
            setInviteCodeInput('');
        } else {
            console.error('No chatroom found with that invite code');
        }
    };

    return (
        <div>
            <h2>Chatrooms</h2>
            <input
                type="text"
                value={inviteCodeInput}
                onChange={(e) => setInviteCodeInput(e.target.value)}
                placeholder="Enter invite code"
            />
            <button onClick={handleAddUserToChatroom}>Add to Chatroom</button>

            <ul>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    chatrooms.map(chatroom => (
                        <li key={chatroom.roomId}>
                            <strong>ID:</strong> {chatroom.roomId},
                            <strong>Name:</strong> {chatroom.roomName},
                            <strong>Invite Code:</strong> {chatroom.inviteCode}.
                            <button onClick={() => handleDeleteRoom(chatroom.roomId)}>Delete</button>
                            <button onClick={() => handleToggleChatroom(chatroom.roomId)}>Toggle</button>
                        </li>
                    ))
                )}
            </ul>

            <div className="active-chatroom">
                {activeChatroomId && (
                    <DynamicChatroom user={user} isLoggedIn={true} chatroomId={activeChatroomId} />
                )}
            </div>
        </div>
    );
}

export default AdminPanel;
