import React, { useState } from 'react';

function CreateRoomForm({ user }) {
    const [roomId, setRoomId] = useState('');  // This would typically be generated on the server or immediately before submitting.
    const [roomName, setRoomName] = useState('');
    const [statusMessage, setStatusMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Normally, roomId would be generated server-side or just before submission
        // Here, you might want to generate it if not done elsewhere
        const response = await fetch('http://localhost:443/createRoom', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                roomId: roomId,  // Assuming roomId is generated client-side for demonstration; typically not recommended
                customerId: user.id,  // User ID from props
                roomName: roomName,
                groupId: groupId
            })
        });

        if (response.ok) {
            setStatusMessage('Room created successfully!');
        } else {
            const errorText = await response.text();
            setStatusMessage(`Failed to create room: ${errorText}`);
        }
    };

    return (
        <div>
            <h2>Create Room</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={roomName}
                    onChange={e => setRoomName(e.target.value)}
                    placeholder="Room Name"
                    required
                />
                <input
                    type="text"
                    value={groupId}
                    onChange={e => setGroupId(e.target.value)}
                    placeholder="Group ID"
                    required
                />
                <button type="submit">Create Room</button>
            </form>
            {statusMessage && <p>{statusMessage}</p>}
        </div>
    );
}

export default CreateRoomForm;
