import React, { useState, useEffect } from 'react';
import { v4 as uuidv4 } from 'uuid';
import DynamicChatroom from "../../components/DynamicChatroom/DynamicChatroom.tsx";

function AdminPanel({ user }) {
    const [chatrooms, setChatrooms] = useState([]);
    const [loading, setLoading] = useState(true);
    const [newRoomName, setNewRoomName] = useState('');
    const [activeChatroomId, setActiveChatroomId] = useState(null); // State to track the active chatroom

    const fetchChatrooms = async () => {
        try {
            const response = await fetch(`http://localhost:443/admin/roomids?customerId=${user.id}&customerEmail=${user.email}`);
            if (response.ok) {
                const data = await response.json();
                setChatrooms(data);
            } else {
                console.error('Failed to fetch chatrooms:', response.status);
            }
        } catch (error) {
            console.error('Error fetching chatrooms:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchChatrooms();
    }, [user.id]);

    const handleDeleteRoom = async (roomId) => {
        try {
            const response = await fetch('http://localhost:443/deleteRoom', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    roomId,
                    customerId: user.id,
                    customerEmail: user.email
                })
            });

            if (response.ok) {
                fetchChatrooms();
                console.log('Room deleted successfully');
            } else {
                const errorText = await response.text();
                console.error(`Failed to delete room: ${errorText}`);
            }
        } catch (error) {
            console.error('Error deleting room:', error);
        }
    };

    const handleCreateRoom = async () => {
        try {
            const roomId = uuidv4();
            const response = await fetch('http://localhost:443/createRoom', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    roomId,
                    customerId: user.id,
                    customerEmail: user.email,
                    roomName: newRoomName
                })
            });

            if (response.ok) {
                fetchChatrooms();
                console.log('Room created successfully');
            } else {
                const errorText = await response.text();
                console.error(`Failed to create room: ${errorText}`);
            }
        } catch (error) {
            console.error('Error creating room:', error);
        }
    };

    const handleToggleChatroom = (roomId) => {
        setActiveChatroomId(roomId === activeChatroomId ? null : roomId); // Toggle active chatroom ID
    };

    return (
        <div>
            <h2>Chatrooms</h2>
            <input
                type="text"
                value={newRoomName}
                onChange={(e) => setNewRoomName(e.target.value)}
                placeholder="Enter room name"
            />
            <button onClick={handleCreateRoom}>Create Room</button>

            <ul>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    chatrooms.map(chatroom => (
                        <li key={chatroom.roomId}>
                            <strong>ID:</strong> {chatroom.roomId}, <strong>Name:</strong> {chatroom.roomName}
                            <button onClick={() => handleDeleteRoom(chatroom.roomId)}>Delete</button>
                            <button onClick={() => handleToggleChatroom(chatroom.roomId)}>Toggle</button>
                        </li>
                    ))
                )}
            </ul>

            {/* Dedicated div for the dynamic chatroom display */}
            <div className="active-chatroom">
                {activeChatroomId && (
                    <DynamicChatroom user={user} isLoggedIn={true} chatroomId={activeChatroomId} />
                )}
            </div>
        </div>
    );
}

export default AdminPanel;
