import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid'; // Import uuidv4 function from the uuid package

function CreateRoomForm({ user }) {
    const [roomName, setRoomName] = useState('');
    const [statusMessage, setStatusMessage] = useState('');
    const [responseData, setResponseData] = useState(null); // State to store response data

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Generate a UUID for roomId
        const generatedRoomId = uuidv4();

        const response = await fetch('http://localhost:443/createRoom', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                roomId: generatedRoomId,  // Use the generated UUID
                customerId: user.id,
                customerEmail: user.email,
                roomName: roomName,
            })
        });

        if (response.ok) {
            setStatusMessage('Room created successfully!');
            const jsonData = await response.json(); // Parse JSON response
            setResponseData(jsonData); // Update response data state
        } else {
            const errorText = await response.text();
            setStatusMessage(`Failed to create room: ${errorText}`);
        }
    };

    return (
        <div>
            <h2>Create Room</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={roomName}
                    onChange={e => setRoomName(e.target.value)}
                    placeholder="Room Name"
                    required
                />
                <button type="submit">Create Room</button>
            </form>
            {statusMessage && <p>{statusMessage}</p>}
            {/* Render response data if available */}
            {responseData && (
                <div>
                    <h3>Response Data:</h3>
                    <pre>{JSON.stringify(responseData, null, 2)}</pre>
                </div>
            )}
        </div>
    );
}

export default CreateRoomForm;
