import React, { useState, useEffect, useRef } from 'react';
import Ably from 'ably';
import { v4 as uuidv4 } from 'uuid';

function Chatroom({ user, isLoggedIn }) {
    const [messages, setMessages] = useState({});
    const [ablyClient, setAblyClient] = useState(null);
    const [channel, setChannel] = useState(null);

    const [newMessage, setNewMessage] = useState('');
    const [pollOptions, setPollOptions] = useState({});

    const bottomOfChat = useRef(null);

    useEffect(() => {
        if (bottomOfChat.current) {
            bottomOfChat.current.scrollIntoView();
        }
    }, [messages]);

    console.log(user)
    const initializeChat = async () => {
        if (isLoggedIn && !ablyClient) {
            try {
                const response = await fetch('http://localhost:443/getMessages');
                const fetchedMessages = await response.json();
                const parsedMessages = fetchedMessages.map(message => {
                    const { TIMESTAMP, CUSTOMER_ID, CUSTOMER_NAME, MESSAGE } = message;
                    return {
                        timestamp: TIMESTAMP,
                        customer_id: CUSTOMER_ID,
                        customer_name: CUSTOMER_NAME,
                        message: MESSAGE

                    };
                });                
                setMessages(parsedMessages);
                const ablyKey = 'gWdAvw.DxcdmQ:WYmbfWlXmbZBC5UeOKWXretPGWjPUCb_F-_x9-JpME4'
                const client = new Ably.Realtime.Promise({ key: ablyKey });
                const chatChannel = client.channels.get('chat');

                chatChannel.subscribe((message) => {   
                    setMessages(prevMessages => {
                        const index = prevMessages.findIndex(msg => msg.timestamp === message.data.timestamp);
                        if (index !== -1) {
                            const updatedMessages = [...prevMessages];
                            updatedMessages[index] = message.data;
                            return updatedMessages;
                        } else {
                            return [...prevMessages, message.data];
                        }
                    });
                });
                setAblyClient(client);
                setChannel(chatChannel);

            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        }
    };

    useEffect(() => {
        if (isLoggedIn && !ablyClient) {
            initializeChat();
        }
    }, [isLoggedIn]);


    const sendMessage = async () => {
        if (channel && newMessage.trim() !== '') {
            const uuid = uuidv4();
            const userDataSTREAM = {
                timestamp: uuid,
                customer_name: user.name,
                customer_id: user.id,
                message: newMessage


            }; 
            const userDataDB = {
                TIMESTAMP: uuid,
                CUSTOMER_NAME: user.CUSTOMER_NAME,
                CUSTOMER_ID: user.CUSTOMER_ID,
                MESSAGE: newMessage


            }; 
            channel.publish("update", userDataSTREAM);
            fetch('http://localhost:443/sendMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(userDataDB)
            })  
            .then(response => console.log('Message added', response))
            .catch(error => console.error('Failed to add message:', error));
        }
        setNewMessage("")
    };

    return (
        <div className="flex flex-row justify-center items-center">
            <div className="w-min">
                <h1 className='text-2xl mb-2'>User Chatroom Prototype</h1>
                <div className='h-[300px] w-[500px] overflow-scroll snap-y border-2 border-black mb-2'>

                {Object.entries(messages).map(([index, message]) => (
                    <div key={index}>
                        <p className='text-lg pl-2'>{"[" + message.timestamp.slice(0,4) + "] " + message.customer_name + ": " + message.message}</p>
                    </div>
                ))}

                <div ref={bottomOfChat}></div>
                </div>
            </div>
            <div>
                    <textarea className="border-2 border-black w-full min-h-min" type="text" value={newMessage} placeholder="message Chatroom" onChange={(e) => setNewMessage(e.target.value)} />
                    <p> Add Poll Options </p>



                    <button className="border-2 border-black pl-2 pr-2" onClick={sendMessage}>Send</button>
                </div>
        </div>
    );
}

export default Chatroom;
