                });
                setAblyClient(client);
                setChannel(chatChannel);

            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        }
    };

    useEffect(() => {
        if (isLoggedIn && !ablyClient) {
            initializeChat();
        }
    }, [isLoggedIn]);


    const sendMessage = async () => {
        if (channel && newMessage.trim() !== '') {
            const uuid = uuidv4();
            const userData = {
                TIMESTAMP: uuid,
                CUSTOMER_NAME: user.name,
                CUSTOMER_ID: parseInt(user.id),
                MESSAGE: newMessage


            };
            channel.publish("update", userData);
            fetch('http://localhost:443/sendMessage', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(userData)
            })  
            .then(response => console.log('User added successfully:', response))
            .catch(error => console.error('Failed to add user:', error));
        }
    };

    const addUserToDatabase = (googleProfile) => {
        const userData = {
            CUSTOMER_ID: parseInt(googleProfile.id), // Assuming CUSTOMER_ID is a string
            CUSTOMER_EMAIL: googleProfile.email,
            CUSTOMER_NAME: googleProfile.name
        };

        console.log(userData)
        fetch('http://localhost:443/setUsers', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(userData)
        })  
        .then(response => console.log('User added successfully:', response))
        .catch(error => console.error('Failed to add user:', error));
    };


    return (
        <div className="flex flex-row justify-center items-center">
            <div className="w-min">
                <h1 className='text-2xl mb-2'>User Chatroom Prototype</h1>
                <div className='h-[300px] w-[500px] overflow-scroll snap-y border-2 border-black mb-2'>

                {Object.entries(messages).map(([index, message]) => (
                    <div key={index}>
                        <p className='text-lg pl-2'>{message.username + ": " + message.message}</p>
                    </div>
                ))}

                <div ref={bottomOfChat}></div>
                </div>
            </div>
            <div>
                    <textarea className="border-2 border-black w-full min-h-min" type="text" value={newMessage} placeholder="message Chatroom" onChange={(e) => setNewMessage(e.target.value)} />
                    <p> Add Poll Options </p>



                    <button className="border-2 border-black pl-2 pr-2" onClick={sendMessage}>Send</button>
                </div>
        </div>
    );
}

export default Chatroom;
